 */
/**
 * Borders
 */
/**
 * Scrollbars
 */
body {
  background: #2F343F;
}
::-webkit-scrollbar {
  width: 8px;
  background: transparent;
  border: none;
}
::-webkit-scrollbar-track {
  background: #242830;
  border-radius: 8px;
}
::-webkit-scrollbar-thumb {
  cursor: pointer;
  border-radius: 8px;
  border: 1px solid #242830 !important;
  background: #363b48 !important;
}
::-webkit-scrollbar-thumb:hover {
  background: #5294E2 !important;
}
.scrollbar-track {
  width: 14px;
  background: transparent;
  border: none;
}
.scrollbar-track-inner {
  background: #242830;
  border-radius: 14px;
}
.scrollbar-handle {
  cursor: pointer;
  border-radius: 14px;
  border: 1px solid #242830 !important;
  background: #363b48 !important;
}
.scrollbar-handle:hover {
  background: #5294E2 !important;
}
/**
 * Text selections
 */
::selection,
::-webkit-selection {
  background: #5294E2;
  color: #fff;
}
#account-switcher .primary-item .name {
  color: #d1d7df;
}
.account-switcher {
  -webkit-filter: brightness(300%);
  filter: brightness(300%);
}
.scroll-region-content {
  background: #2F343F;
}
.sidebar-activity {
  background: #2F343F;
  box-shadow: none;
  color: #B3BDCA;
}
.account-sidebar-sections {
  background-color: transparent !important;
}
.account-sidebar-sections section:first-child .heading {
  padding-right: 40px;
}
.account-sidebar-sections section .heading {
  padding-bottom: 5px;
}
.account-sidebar-sections section .heading .text {
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
}
.account-sidebar-sections section .item-container {
  margin: 4px 10px 0 0 !important;
}
.account-sidebar-sections section .item-container .disclosure-triangle {
  display: flex;
  align-items: center;
  width: 15px;
}
.account-sidebar-sections section .item-container .disclosure-triangle div {
  border-left-color: #495668;
  border-top-width: 3px;
  border-bottom-width: 3px;
  border-left-width: 5px;
  transform-origin: 2px;
}
.account-sidebar-sections section .item-container .item {
  padding: 0 10px !important;
  color: #B3BDCA !important;
  cursor: pointer !important;
}
.account-sidebar-sections section .item-container .item .item-count-box {
  background: transparent !important;
  color: #B3BDCA !important;
  box-shadow: 0 0.5px 0 #B3BDCA, 0 -0.5px 0 #B3BDCA, 0.5px 0 0 #B3BDCA, -0.5px 0 0 #B3BDCA !important;
}
.account-sidebar-sections section .item-container .item.selected {
  background: #5294E2 !important;
  border-radius: 4px;
  color: #FFFFFF !important;
}
.account-sidebar-sections section .item-container .item.selected .item-count-box {
  background: #FFFFFF !important;
  color: #5294E2 !important;
  box-shadow: 0 0.5px 0 #FFFFFF, 0 -0.5px 0 #FFFFFF, 0.5px 0 0 #FFFFFF, -0.5px 0 0 #FFFFFF !important;
}
.account-sidebar-sections section .item-container .item.selected .icon img {
  background: #FFFFFF !important;
}
