css:
/**
 * Colors
 */
/**
 * Borders
 */
.list-tabular .list-column.list-column-Item {
  margin-left: -20px;
  padding-left: 30px;
}
.list-item.list-tabular-item .btn.action {
  box-shadow: none !important;
  border: none !important;
  -webkit-filter: invert(100%) brightness(300%) !important;
  filter: invert(100%) brightness(300%) !important;
}
.list-item.list-tabular-item.focused .btn.action {
  -webkit-filter: invert(100%) brightness(300%) !important;
  filter: invert(100%) brightness(300%) !important;
}
.list-item.list-tabular-item.keyboard-cursor {
  border-left: 4px solid #5294E2;
}
.list-item.list-tabular-item.keyboard-cursor.selected {
  border-left: 4px solid #FFFFFF;
}
.list-item.list-tabular-item .checkmark .inner {
  border-color: #495668;
}
.list-item.list-tabular-item.selected .checkmark .inner {
  background-color: transparent;
  border: 1px solid #FFFFFF;
}
.list-item.list-tabular-item .details .snippet {
  opacity: .35;
}
.open-tracking-icon .content-mask {
  background-color: rgba(255, 255, 255, 0.35) !important;
}
.thread-list .timestamp,
.draft-list .timestamp {
  opacity: 1;
}
.thread-list .list-container .list-item,
.draft-list .list-container .list-item {
  /*
			.list-column {
				border-bottom: 0 !important;
				padding-right: 4px !important;
				padding-left: 8px;
			}
			*/
}
.thread-list .list-container .list-item .thread-icon,
.draft-list .list-container .list-item .thread-icon,
.thread-list .list-container .list-item .mail-important-icon,
.draft-list .list-container .list-item .mail-important-icon,
.thread-list .list-container .list-item .draft-icon,
.draft-list .list-container .list-item .draft-icon {
  -webkit-filter: brightness(100) !important;
  filter: brightness(100) !important;
}
.thread-list .list-container .list-item .thread-icon-star,
.draft-list .list-container .list-item .thread-icon-star {
  -webkit-filter: invert(1) brightness(999999999) !important;
  filter: invert(1) brightness(999999999) !important;
}
.thread-list .list-container .list-item.focused:hover .list-column-HoverActions .inner,
.draft-list .list-container .list-item.focused:hover .list-column-HoverActions .inner {
  color: #FFFFFF !important;
  background-image: linear-gradient(90deg, rgba(47, 52, 63, 0) 0%, #282d36 35%);
}
.thread-list .list-container .list-item.focused:hover .list-column-HoverActions .inner .action,
.draft-list .list-container .list-item.focused:hover .list-column-HoverActions .inner .action {
  -webkit-filter: none;
  filter: none;
}
.thread-list .list-container .list-item.focused:hover .list-column-HoverActions .inner .thread-icon:not(.thread-icon-star),
.draft-list .list-container .list-item.focused:hover .list-column-HoverActions .inner .thread-icon:not(.thread-icon-star) {
  opacity: 0.7;
}
.thread-list .list-container .list-item.focused,
.draft-list .list-container .list-item.focused {
  border-bottom: 0;
}
.thread-list .list-container .list-item:hover .thread-icon,
.draft-list .list-container .list-item:hover .thread-icon {
  visibility: inherit;
}
.thread-list .list-container .list-item:hover .list-column-HoverActions .inner,
.draft-list .list-container .list-item:hover .list-column-HoverActions .inner {
  color: #FFFFFF !important;
  background-image: linear-gradient(90deg, rgba(47, 52, 63, 0) 0%, #2F343F 35%);
}
.thread-list .list-container .list-item:hover .list-column-HoverActions .inner .action,
.draft-list .list-container .list-item:hover .list-column-HoverActions .inner .action {
  -webkit-filter: none;
  filter: none;
}
.thread-list .list-container .list-item:hover .list-column-HoverActions .inner .thread-icon:not(.thread-icon-star),
.draft-list .list-container .list-item:hover .list-column-HoverActions .inner .thread-icon:not(.thread-icon-star) {
  opacity: 0.7;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item {
  cursor: pointer !important;
  box-sizing: border-box;
  background-color: #2F343F !important;
  color: #8f98ab !important;
  border-bottom: 1px solid #282d36;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.unread,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.unread {
  color: #d1d7df !important;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item .participants.no-recipients,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item .participants.no-recipients {
  color: inherit;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item .mail-label,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item .mail-label {
  -webkit-filter: none;
  filter: none;
  margin-right: 4px;
  font-size: 0.85em;
  color: #B3BDCA !important;
  background: #282d36 !important;
  box-shadow: none !important;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.focused,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.focused {
  color: #B3BDCA !important;
  background-color: #282d36 !important;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.focused .mail-label,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.focused .mail-label {
  color: #5294E2 !important;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.selected,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.selected {
  color: #FFFFFF !important;
  background-color: #458ce0 !important;
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.selected .list-column-HoverActions .inner,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.selected .list-column-HoverActions .inner {
  color: #FFFFFF !important;
  background-image: linear-gradient(90deg, rgba(69, 140, 224, 0) 0%, #458ce0 35%);
}
.thread-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.selected .participants.no-recipients,
.draft-list .list-container .scroll-region-content .scroll-region-content-inner .list-rows .list-item.selected .participants.no-recipients {
  color: #FFFFFF;
  opacity: 0.35;
}
.thread-list .thread-icon,
.draft-list .thread-icon {
  background-image: url(../static/images/thread-list/icon-star-hover-@2x.png);
}
.thread-list .thread-icon:not(.thread-icon-star),
.draft-list .thread-icon:not(.thread-icon-star) {
  visibility: hidden;
}
.is-blurred .thread-list .list-container .list-item.focused {
  border-bottom: 0;
}
.thread-list-empty-folder-bar {
  background: #242830;
  border: none;
  color: #8f98ab;
  padding: 8px;
  border-radius: 4px;
}
.thread-list-empty-folder-bar .btn {
  background: #5294E2;
  color: #FFFFFF;
  transition: all 0.25s ease-in-out;
}
.thread-list-empty-folder-bar .btn:hover {
  background: #FFFFFF;
  color: #242830;
}
.layout-mode-list .sheet-toolbar .toolbar-ThreadList .absolute,
.layout-mode-list .sheet-toolbar .toolbar-DraftList .absolute {
  border-color: transparent;
  background: #2F343F;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container {
  background: #282d36;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item {
  color: #7789a0;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item span .primary {
  color: inherit;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item span .secondary {
  color: #495668;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item:hover {
  color: #B3BDCA;
  background: #242830;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item.divider {
  background: #282d36 !important;
  color: #95a3b5;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item.selected {
  border-radius: 4px;
}
.thread-search-bar .menu.showing-query.search-container.showing-suggestions .content-container .item.selected .secondary {
  color: rgba(255, 255, 255, 0.35) !important;
}
.layout-mode-list .thread-list .list-container .list-item .list-column,
.layout-mode-list .draft-list .list-container .list-item .list-column {
  padding-left: 12px !important;
  padding-right: 12px !important;
}
.layout-mode-list .scroll-region-content {
  width: 99%;
}
.layout-mode-split .thread-list .list-container .list-item .list-column,
.layout-mode-split .draft-list .list-container .list-item .list-column {
  padding-left: 4px !important;
  padding-right: 8px !important;
}
.layout-mode-split .scroll-region-content {
  width: 97%;
}
