 */
/**
 * Borders
 */
.item-count-box {
  min-width: 15px;
  height: 15px;
  text-align: center;
  display: inline;
  font-size: 10.5px;
  padding: 0 0.286em 0 0.286em;
  line-height: 15px;
  border-radius: 2px;
  align-self: center;
}
body.platform-win32 .item-count-box {
  border-radius: 0;
}
.windows-btn-bg {
  transition: background 300ms, box-shadow 300ms;
  background: transparent;
  border-radius: 0;
}
.windows-btn-bg:hover {
  background: rgba(25, 28, 34, 0.8);
}
.windows-btn-border {
  border: 0;
  box-shadow: 0 0 0 2px rgba(25, 28, 34, 0.8);
}
.account-sidebar {
  flex: 1;
  height: 100%;
  background-color: #2F343F;
}
.account-sidebar .item.deleted {
  opacity: 0.5;
}
.account-sidebar .outline-view:first-child .heading span {
  margin-right: 30px;
}
.account-switcher {
  position: absolute;
  top: 7px;
  right: 17px;
  z-index: 3;
}
.account-switcher img {
  transform: rotateX(180deg);
}
