css:
/**
 * Colors
 */
/**
 * Borders
 */
.thread-list .list-item .list-column-HoverActions .action.action-snooze {
  background: url('../static/images/thread-list-quick-actions/ic-quickaction-snooze@2x.png') center no-repeat, none;
}
.snooze-button {
  order: -103;
}
.snooze-popover {
  color: rgba(179, 189, 202, 0.8);
  display: flex;
  flex-direction: column;
}
.snooze-popover .snooze-row {
  display: flex;
}
.snooze-popover .snooze-row .snooze-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 15px 0;
  cursor: default;
  width: 105px;
  line-height: initial;
  text-align: initial;
}
.snooze-popover .snooze-row .snooze-item img {
  background-color: rgba(179, 189, 202, 0.8);
}
.snooze-popover .snooze-row .snooze-item:hover {
  background-color: #242830;
  color: rgba(179, 189, 202, 0.9);
}
.snooze-popover .snooze-row .snooze-item:hover img {
  background-color: rgba(179, 189, 202, 0.9);
}
.snooze-popover .snooze-row .snooze-item:active {
  background-color: #1e2128;
  color: #b3bdca;
}
.snooze-popover .snooze-row .snooze-item:active img {
  background-color: #b3bdca;
}
.snooze-popover .snooze-row .snooze-item + .snooze-item {
  border-left: 1px solid #191c22;
}
.snooze-popover .snooze-row + .snooze-row {
  border-top: 1px solid #191c22;
}
.snooze-popover .snooze-input {
  border-top: 1px solid #191c22;
  padding: 9px 16px;
}
.snooze-popover .snooze-input input {
  margin-bottom: 3px;
}
