css:
/**
 * Colors
 */
/**
 * Borders
 */
.find-in-thread {
  background: #2F343F;
  text-align: right;
  overflow: hidden;
  height: 0;
  padding: 0 8px;
  transition: all 125ms ease-in-out;
  border-bottom: 0;
}
.find-in-thread.enabled {
  padding: 4px 8px;
  height: 35px;
  border-bottom: 1px solid #191c22;
}
.find-in-thread .controls-wrap {
  display: inline-block;
}
.find-in-thread .selection-progress {
  color: #B3BDCA;
  position: absolute;
  top: 4px;
  right: 54px;
  font-size: 12px;
}
.find-in-thread .btn.btn-find-in-thread {
  border: 0;
  box-shadow: 0 0 0;
  border-radius: 0;
  background: transparent;
  display: inline-block;
}
.find-in-thread .input-wrap {
  display: inline-block;
  position: relative;
}
.find-in-thread .input-wrap input {
  height: 26px;
  width: 230px;
  padding-left: 8px;
  font-size: 12px;
}
.find-in-thread .input-wrap .btn-wrap {
  min-width: 54px;
  position: absolute;
  top: 0;
  right: 0;
}
