css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.selected-items-stack {
  display: flex;
  align-self: center;
  align-items: center;
  height: 100%;
}
.selected-items-stack .selected-items-stack-content {
  display: flex;
  position: relative;
  align-items: center;
  justify-content: center;
  width: 198px;
  height: 268px;
}
.selected-items-stack .selected-items-stack-content .stack .card {
  position: absolute;
  top: 0;
  left: 0;
  width: 198px;
  height: 268px;
}
.selected-items-stack .selected-items-stack-content .stack .card svg {
  stroke: #374850;
  fill: #222D32;
}
.selected-items-stack .selected-items-stack-content .count-info {
  display: flex;
  flex-direction: column;
  align-items: center;
  z-index: 6;
}
.selected-items-stack .selected-items-stack-content .count-info .count {
  font-size: 4em;
  font-weight: 200;
  color: rgba(238, 238, 238, 0.6);
}
.selected-items-stack .selected-items-stack-content .count-info .count-message {
  padding-top: 5px;
  color: rgba(238, 238, 238, 0.6);
}
.selected-items-stack .selected-items-stack-content .count-info .clear.btn {
  margin-top: 18px;
}
