css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.thread-sharing-button {
  order: -102;
}
.thread-sharing-button.active img.content-mask {
  background-color: #597ea0;
}
.fixed-popover .thread-sharing-popover {
  position: relative;
  width: 280px;
}
.fixed-popover .thread-sharing-popover .share-toggle {
  padding: 7px 10px;
}
.fixed-popover .thread-sharing-popover .share-toggle .meta {
  font-size: 12.04px;
  padding-left: 19px;
}
.fixed-popover .thread-sharing-popover .share-toggle input {
  margin-right: 7px;
}
.fixed-popover .thread-sharing-popover .share-input {
  border-top: 1px solid rgba(0, 0, 0, 0.15);
  padding: 10px;
  padding-bottom: 0;
}
.fixed-popover .thread-sharing-popover .share-input input[type='text'] {
  cursor: default;
  user-select: all;
}
.fixed-popover .thread-sharing-popover .share-controls {
  padding: 10px;
}
.fixed-popover .thread-sharing-popover.disabled .share-input input[type='text'] {
  color: rgba(238, 238, 238, 0.6);
  user-select: none;
}
.fixed-popover .thread-sharing-popover.disabled button.btn {
  color: rgba(238, 238, 238, 0.6);
}
