css:
/**
 * Colors
 */
/**
 * Borders
 */
.header-container {
  margin-right: 10px;
}
select {
  background: transparent;
  padding: 4px;
  border-radius: 4px;
}
body .sheet-toolbar {
  border-bottom: none;
}
body .sheet-toolbar .sheet-toolbar-container {
  background: #2F343F !important;
}
body .sheet-toolbar .sheet-toolbar-container .item-compose .content-mask {
  background: #d1d7df;
}
body .sheet-toolbar .sheet-toolbar-container .activity-toolbar-icon.content-mask {
  background: #5294E2;
}
body .sheet-toolbar .sheet-toolbar-container .toolbar-Preferences {
  background: #282d36;
  border-bottom: 1px solid #282d36 !important;
}
.composer-centered .composer-participant-field:after,
.composer-centered .composer-subject:after {
  background: #2b2f39;
}
.composer-centered .collapsed-composer-participants {
  border-color: #2b2f39 !important;
}
.composer-participant-field .only-item {
  background: transparent !important;
}
.sheet {
  background: #2F343F !important;
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  background: #242830 !important;
  box-shadow: none !important;
  border: none !important;
}
.composer-inner-wrap .composer-action-bar-wrap {
  background: #242830 !important;
  box-shadow: none !important;
  border: none;
}
.tooltip .scroll-tooltip {
  background: #5294E2;
  border: none;
}
.tooltip .scroll-tooltip::after {
  border-left-color: #5294E2;
  border-width: 10px;
  margin-left: -1px;
  margin-top: -10px;
}
/**
 * Buttons
 */
.btn-toolbar,
.token,
.actions > .btn,
.new-package > .btn,
.appearance-mode-switch > .btn {
  /* Background of button should be @white */
  box-shadow: none !important;
  background: transparent !important;
  border: none;
  border-radius: 4px !important;
  transition: all 0.15s ease-in-out;
}
.btn-toolbar.item-compose,
.token.item-compose,
.actions > .btn.item-compose,
.new-package > .btn.item-compose,
.appearance-mode-switch > .btn.item-compose {
  border: none;
  background: transparent !important;
}
.btn,
a,
button {
  transition: all 0.15s ease-in-out;
}
.composer-action-bar-content .btn-toolbar {
  border: 0 !important;
}
.button-dropdown.bordered .primary-item {
  background: #5294E2 !important;
  color: #FFFFFF;
  border-color: #498fe1 !important;
}
body.platform-win32 .sheet-toolbar-container .btn-toolbar {
  border: 0 !important;
}
.btn.btn-emphasis {
  background-color: #5294E2 !important;
  border-color: #5294E2 !important;
  color: #FFFFFF !important;
}
.btn.btn-emphasis img.content-mask {
  background-color: #FFFFFF !important;
}
.button-dropdown.bordered .primary-item {
  box-shadow: none !important;
  background: #5294E2 !important;
  border: 1px solid #498fe1 !important;
  border-top-left-radius: 4px !important;
  border-bottom-left-radius: 4px !important;
}
.button-dropdown.bordered .primary-item img {
  position: relative;
  top: -2px;
}
.button-dropdown.bordered .secondary-picker {
  box-shadow: none !important;
  background: #5294E2 !important;
  border: 1px solid #498fe1 !important;
  border-top-right-radius: 4px !important;
  border-bottom-right-radius: 4px !important;
  margin-left: -1px !important;
}
.button-dropdown.bordered .secondary-items .item {
  color: #B3BDCA !important;
}
.button-dropdown.bordered .secondary-items .item .search-match {
  background: #FFFFFF !important;
}
.button-dropdown.bordered .secondary-items .item .button-dropdown img {
  background: #B3BDCA !important;
}
.sheet-toolbar .btn-toolbar {
  height: 2em !important;
  line-height: 1 !important;
  margin-top: 6px !important;
}
.sheet-toolbar .btn-toolbar .content-mask {
  background-color: #B3BDCA !important;
  transition: all 0.25s ease-in-out;
}
.sheet-toolbar .btn-toolbar:hover .content-mask {
  background-color: #FFFFFF !important;
}
/**
 * Feedback button
 */
.btn-feedback {
  background: #5294E2 !important;
  border: none !important;
}
/* Vertical panel handles */
.flexbox-handle-horizontal div {
  box-shadow: none;
}
/**
 * Dropdown
 */
.thread-search-bar .menu .item.selected .primary {
  color: #FFFFFF !important;
}
.thread-search-bar .menu .item.selected .secondary {
  color: #d1d7df !important;
}
.thread-search-bar .menu.search-container input {
  background: none;
  border: none;
  box-shadow: none;
}
.thread-search-bar .menu.search-container .item {
  background: #2b2f39 !important;
  color: #B3BDCA;
}
.thread-search-bar .menu.search-container .item.selected {
  background: #5294E2 !important;
  color: #FFFFFF !important;
}
.thread-search-bar .menu.search-container .search-accessory {
  -webkit-filter: brightness(60%) saturate(7);
  filter: brightness(60%) saturate(7);
}
.thread-search-bar .menu.search-container .search-accessory:last-child {
  -webkit-filter: invert(100);
  filter: invert(100);
}
.thread-search-bar.focused .menu.search-container .search-accessory {
  -webkit-filter: brightness(80%) saturate(7);
  filter: brightness(80%) saturate(7);
}
.thread-search-bar.focused .menu.search-container .search-accessory:last-child {
  -webkit-filter: invert(100);
  filter: invert(100);
}
::-webkit-input-placeholder {
  /* Chrome/Opera/Safari */
  color: #495668;
}
/**
 * Plugin page
 */
.package {
  border-radius: 4px;
}
/**
 * Prefs page
 */
.appearance-mode {
  background-color: transparent !important;
  border: 1px solid #5b6479 !important;
  color: #B3BDCA;
}
.appearance-mode img.content-mask {
  background-color: #FFFFFF !important;
}
.appearance-mode.active {
  background-color: #5294E2 !important;
  color: #FFFFFF !important;
  border: 1px solid #FFFFFF !important;
}
.preferences-wrap .btn {
  border-radius: 4px;
  background: transparent;
  color: #B3BDCA;
  border: 1px solid #B3BDCA;
  box-shadow: none;
}
.preferences-wrap .btn:hover {
  color: #2F343F;
  border: 1px solid #FFFFFF;
  background: #FFFFFF;
}
.preferences-wrap .container-preference-tabs {
  border-bottom: 1px solid #3a404e !important;
}
.preferences-wrap .container-preference-tabs .preferences-tabs {
  background: #282d36 !important;
  border-bottom: none !important;
}
.preferences-wrap .container-preference-tabs .preferences-tabs .item.active {
  background: #2F343F;
  border: 1px solid #262a33;
  border-bottom: 2px solid #2F343F;
  margin-bottom: -1px;
}
.preferences-wrap .preferences-content.scroll-region {
  background: #2F343F !important;
}
.preferences-wrap .preferences-content.scroll-region .items-wrapper {
  border: 1px solid #5b6479 !important;
  background: none;
}
.preferences-wrap .preferences-content.scroll-region .identity-content-box {
  color: #B3BDCA;
  border-radius: 4px;
  border: 1px solid #5b6479 !important;
}
.preferences-wrap .preferences-content.scroll-region .identity-content-box .padded {
  border-top: 1px solid #5b6479 !important;
}
.preferences-wrap .preferences-content.scroll-region .identity-content-box .padded:first-child {
  border: none !important;
}
.preferences-wrap .container-general .local-data .btn {
  border-radius: 4px;
  margin: 4px 8px 0 0;
  background: transparent;
}
/**
 * Tutorial bubble
 */
.tutorial-tip.visible {
  background: #5294E2;
}
.tutorial-tip.visible:after {
  border: 1px solid #5294E2;
}
