css:
/**
 * Colors
 */
/**
 * Borders
 */
.column-MessageList {
  background: #2F343F;
}
#sheet-container {
  background: none !important;
  margin-right: -6px;
  padding-right: 6px;
}
#message-list .footer-reply-area-wrap {
  background: transparent;
  border: none;
  box-shadow: none;
}
#message-list .thread-icon,
#message-list .mail-important-icon,
#message-list .draft-icon {
  -webkit-filter: brightness(100) !important;
  filter: brightness(100) !important;
  align-self: flex-start;
}
#message-list .message-header {
  position: relative;
  padding: 19px 0;
}
#message-list .message-header::before {
  position: absolute;
  top: 0;
  left: -20px;
  right: -20px;
  height: 100%;
  content: "";
  display: block;
  background: #2b2f39;
  border-radius: 4px 4px 0 0;
  z-index: -9999;
}
#message-list .message-subject-wrap {
  line-height: 1.65em !important;
}
#message-list .message-subject-wrap .message-subject {
  line-height: 1.65em !important;
}
#message-list .message-subject-wrap .message-icons-wrap {
  align-self: flex-end;
}
#message-list .message-item-wrap .message-item-white-wrap {
  background: #333945;
  color: #B3BDCA;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap {
  background: #2b2f39 !important;
}
#message-list .message-item-wrap .message-item-white-wrap.composer-outer-wrap.focused {
  box-shadow: none;
}
#message-list .message-item-wrap.collapsed .message-item-white-wrap {
  background: #2b2f39;
  color: #7789a0;
}
#message-list .minified-bundle .msg-line {
  background: #2b2f39;
  border-color: #262a33;
  color: #7789a0;
}
#message-list .minified-bundle .num-messages {
  background: #2F343F;
  border-color: #262a33;
  color: #7789a0;
}
#message-list .mail-label.removable {
  -webkit-filter: none;
  filter: none;
  margin-right: 4px;
  font-size: 0.85em;
  color: #B3BDCA !important;
  background: #282d36 !important;
  box-shadow: none !important;
}
#message-list .mail-label.removable:hover {
  color: #FFFFFF !important;
}
#message-list .mail-label.removable:hover img.x.content-mask {
  background-color: #FFFFFF !important;
}
#message-list .button-dropdown.bordered > div {
  background: transparent !important;
  border-color: #5b6479 !important;
  color: #B3BDCA !important;
}
#message-list .button-dropdown.bordered > div img.content-mask {
  background-color: #B3BDCA !important;
}
#message-list .button-dropdown.bordered > div:hover img.content-mask {
  background-color: #FFFFFF !important;
}
#message-list .button-dropdown.bordered:hover > div {
  color: #FFFFFF !important;
}
#message-list .button-dropdown.bordered:hover > div img.content-mask {
  background-color: #FFFFFF !important;
}
#message-list .button-dropdown.bordered .secondary-items .menu .item {
  background: #3a404e;
}
#message-list .button-dropdown.bordered .secondary-items .menu .item:hover {
  background: #5294E2 !important;
  color: #FFFFFF !important;
}
#message-list .button-dropdown.bordered .secondary-items .menu .item:hover img.content-mask {
  background-color: #FFFFFF !important;
}
.button-dropdown.btn-emphasis .primary-item img.content-mask,
.button-dropdown.btn-emphasis .secondary-picker img.content-mask,
.button-dropdown.btn-emphasis .only-item img.content-mask {
  background-color: #FFFFFF !important;
}
.contenteditable-container spelling.misspelled {
  background-image: linear-gradient(45deg, transparent, transparent 49%, rgba(255, 255, 255, 0.35) 49%, transparent 51%);
}
.signature-button-dropdown .menu > div {
  border: none !important;
}
.signature-button-dropdown .menu .item.checked {
  background-image: none !important;
}
.column-MessageListSidebar {
  border: none;
  color: #B3BDCA;
  margin-right: 6px;
}
.column-MessageListSidebar div[name=unsafe-component-wrapper] {
  order: 2 !important;
}
.column-MessageListSidebar .sidebar-participant-picker select {
  background: transparent;
  border: 1px solid transparent;
}
.column-MessageListSidebar .participant-profile .profile-photo-wrap {
  width: 70px;
  height: 70px;
  padding: 5px;
  top: -46px;
  left: calc(50% - 35px);
  border-radius: 100%;
  background: #282d36;
  box-shadow: none;
}
.column-MessageListSidebar .participant-profile .profile-photo-wrap .profile-photo,
.column-MessageListSidebar .participant-profile .profile-photo-wrap .default-profile-image {
  line-height: 60px;
  width: 60px;
  height: 60px;
  border-radius: 100%;
  box-shadow: none;
  background-image: none;
}
.column-MessageListSidebar .participant-profile .profile-photo-wrap .personal-info {
  padding-top: 50px;
}
.column-MessageListSidebar .sidebar-section {
  background: #282d36;
  border-radius: 4px;
  border: none;
  margin-top: -4px;
}
.column-MessageListSidebar .sidebar-section .related-threads {
  margin-top: 0;
}
.column-MessageListSidebar .sidebar-section .related-threads .related-thread:hover {
  background: #5294E2;
  color: #FFFFFF;
}
.column-MessageListSidebar .sidebar-section .btn {
  background: transparent;
  border: 1px solid #FFFFFF;
  box-shadow: none;
}
.column-MessageListSidebar .sidebar-section .btn:hover,
.column-MessageListSidebar .sidebar-section .btn:active {
  background: #FFFFFF;
  color: #2F343F;
}
iframe {
  overflow: hidden !important;
}
.ignore-in-parent-frame body {
  color: #B3BDCA;
  overflow: hidden !important;
}
.ignore-in-parent-frame img {
  color: #B3BDCA;
}
.ignore-in-parent-frame blockquote {
  border-color: #5b6479 !important;
}
.ignore-in-parent-frame a {
  transition: all 0.15s ease-in-out;
  background: none !important;
}
.ignore-in-parent-frame .link-tracking-dot {
  display: none;
}
.ignore-in-parent-frame hr {
  border: none;
  border-top: 1px solid #495668;
}
.attachments-area .inner {
  background: #2F343F;
  color: #d1d7df;
}
.item-back .item-back-title {
  color: #B3BDCA !important;
}
.item-back .content-mask {
  background-color: #B3BDCA !important;
}
