css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.category-mapper-container {
  width: 40%;
  min-width: 460px;
  margin: 0 auto;
}
.account-section-title {
  border-bottom: 1px solid #374850;
  margin: 13.5px 0;
}
input.search {
  border: 1px solid #0d1214;
  border-radius: 3px;
  background-color: #222D32;
  box-shadow: inset 0 1px 0 rgba(0, 0, 0, 0.05), 0 1px 0 rgba(0, 0, 0, 0.05);
  color: #eee;
  padding-left: 0;
  background-repeat: no-repeat;
  background-image: url('../static/images/search/searchloupe@2x.png');
  background-size: 15px 15px;
  background-position: 7px 4px;
  text-indent: 31px;
}
.role-section {
  display: flex;
}
.role-section .col-left {
  color: rgba(238, 238, 238, 0.6);
  text-align: right;
  flex: 1;
  margin-right: 20px;
}
.role-section .col-right {
  text-align: left;
  margin-bottom: 5px;
  flex: 1;
}
