css:
a {
  color: #74b8d7;
}
input[type="text"],
input[type="email"],
input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="month"],
input[type="number"],
input[type="password"],
input[type="range"],
input[type="search"],
input[type="tel"],
input[type="time"],
input[type="url"] {
  background: #263238;
  border: 1px solid rgba(221, 221, 221, 0.1);
  color: #607D8B;
}
.menu .item.selected,
.menu .item:active {
  background-color: #7ab7b4;
}
.button-dropdown.btn-emphasis .primary-item,
.button-dropdown.btn-emphasis .secondary-picker,
.button-dropdown.btn-emphasis .only-item {
  background: #F77669;
  border: 1px solid #F77669;
}
.scrollbar-track {
  background: none;
  border-left: none;
  width: 10px;
}
.scrollbar-track .scrollbar-handle {
  background: rgba(221, 221, 221, 0.1);
  border: none;
}
.sheet-toolbar {
  border-bottom: none;
}
.sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(#263238, #263238, #263238);
}
.sheet-toolbar .btn-toolbar {
  background: none;
  box-shadow: none;
}
.sheet-toolbar .search-bar .menu .header-container input {
  box-shadow: 0 0.5px 0 rgba(221, 221, 221, 0.1), 0 -0.5px 0 rgba(221, 221, 221, 0.1), 0.5px 0 0 rgba(221, 221, 221, 0.1), -0.5px 0 0 rgba(221, 221, 221, 0.1);
}
body.is-blurred .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(#263238, #263238, #263238);
}
body.is-blurred .sheet-toolbar-container .btn.btn-toolbar {
  background: none;
  box-shadow: none;
}
body.is-blurred .search-bar .menu .header-container input {
  box-shadow: 0 0.5px 0 rgba(221, 221, 221, 0.1), 0 -0.5px 0 rgba(221, 221, 221, 0.1), 0.5px 0 0 rgba(221, 221, 221, 0.1), -0.5px 0 0 rgba(221, 221, 221, 0.1);
}
.thread-list .list-item,
.draft-list .list-item {
  background: #263238;
  border-bottom: 1px solid rgba(221, 221, 221, 0.1);
}
.thread-list .list-item:hover,
.draft-list .list-item:hover {
  background: #263238;
}
.thread-list .list-item:hover .mail-important-icon,
.draft-list .list-item:hover .mail-important-icon {
  opacity: 0.2;
}
.thread-list .list-item.focused:hover,
.draft-list .list-item.focused:hover {
  background: #7ab7b4;
}
.thread-list .unread:not(.focused):not(.selected):not(.next-is-selected),
.draft-list .unread:not(.focused):not(.selected):not(.next-is-selected) {
  border-bottom: 1px solid rgba(221, 221, 221, 0.1);
}
.thread-list .unread:not(.focused):not(.selected):not(.next-is-selected):hover,
.draft-list .unread:not(.focused):not(.selected):not(.next-is-selected):hover {
  background: #263238;
}
.list-container .list-item.focused {
  border-bottom: 1px solid transparent;
}
.list-container .list-item.focused:hover {
  border-bottom: 1px solid transparent;
}
.thread-list .swipe-backing.swipe-all,
.thread-list .swipe-backing.swipe-archive,
.draft-list .swipe-backing.swipe-all,
.draft-list .swipe-backing.swipe-archive {
  background: #b9dc87;
}
.thread-list .swipe-backing.swipe-all.confirmed,
.thread-list .swipe-backing.swipe-archive.confirmed,
.draft-list .swipe-backing.swipe-all.confirmed,
.draft-list .swipe-backing.swipe-archive.confirmed {
  background: #bae47f;
}
.thread-list .swipe-backing.swipe-snooze,
.draft-list .swipe-backing.swipe-snooze {
  background: #af82cf;
}
.thread-list .swipe-backing.swipe-snooze.confirmed,
.draft-list .swipe-backing.swipe-snooze.confirmed {
  background: #b079d8;
}
.thread-list .swipe-backing.swipe-trash,
.draft-list .swipe-backing.swipe-trash {
  background: #d74962;
}
.thread-list .swipe-backing.swipe-trash.confirmed,
.draft-list .swipe-backing.swipe-trash.confirmed {
  background: #e23e5b;
}
.undo-redo {
  background-color: #7ab7b4;
  color: #ffffff;
}
.undo-redo-message-wrapper {
  color: #ffffff !important;
}
.undo-redo-action-wrapper img {
  background-color: #ffffff !important;
}
.undo-redo-action-wrapper .undo-redo-action-text {
  color: #ffffff !important;
}
body.is-blurred .list-container .list-item.focused {
  background: #7ab7b4;
  color: #ffffff;
  border-bottom: 1px solid #7ab7b4;
}
.find-in-thread {
  border-bottom: none;
}
#message-list .message-item-wrap .message-item-white-wrap,
#message-list .footer-reply-area-wrap {
  box-shadow: 0 0 0.5px rgba(221, 221, 221, 0.1), 0 1px 1.5px rgba(221, 221, 221, 0.1);
}
#message-list .show-hidden-messages {
  border: none;
}
.button-dropdown .secondary-picker {
  box-shadow: rgba(221, 221, 221, 0.1) 0 0.5px 0, rgba(221, 221, 221, 0.1) 0 -0.5px 0, rgba(221, 221, 221, 0.1) 0.5px 0 0, rgba(221, 221, 221, 0.1) 0 0.5px 1px;
}
.button-dropdown .primary-item,
.button-dropdown .only-item {
  box-shadow: 0 0.5px 0 rgba(221, 221, 221, 0.1), 0 -0.5px 0 rgba(221, 221, 221, 0.1), 0.5px 0 0 rgba(0, 0, 0, 0.15), -0.5px 0 0 rgba(221, 221, 221, 0.1), 0 0.5px 1px rgba(221, 221, 221, 0.1);
}
.composer-full-window .composer-inner-wrap .composer-action-bar-wrap {
  border-top: 1px solid rgba(221, 221, 221, 0.1);
}
.column-MessageListSidebar {
  border-left: none;
}
.column-MessageListSidebar .sidebar-section.visible {
  background-color: #7ab7b4;
  color: #ffffff !important;
}
.column-MessageListSidebar .sidebar-section.visible .participant-profile .personal-info .email,
.column-MessageListSidebar .sidebar-section.visible .participant-profile .additional-info .bio {
  color: #ffffff !important;
}
.column-MessageListSidebar .sidebar-section.visible .related-threads {
  background-color: #59a5a1;
}
.column-MessageListSidebar .sidebar-section.visible .related-threads .subject,
.column-MessageListSidebar .sidebar-section.visible .related-threads .timestamp,
.column-MessageListSidebar .sidebar-section.visible .related-threads .toggle,
.column-MessageListSidebar .sidebar-section.visible .related-threads .content {
  color: #ffffff !important;
}
.column-MessageListSidebar .sidebar-section.visible .related-threads .related-thread:hover {
  background-color: rgba(221, 221, 221, 0.1);
}
.preferences-wrap .appearance-mode-switch .appearance-mode {
  background: rgba(221, 221, 221, 0.1);
}
.preferences-wrap .preferences-accounts .accounts-content .account-details {
  background-color: transparent;
}
.preferences-wrap .container-templates .template-wrap {
  border: 1px solid rgba(221, 221, 221, 0.1);
}
.preferences-wrap .templates-instructions {
  color: #607D8B;
}
#Threads .undo-redo-toast .content .message {
  color: #ffffff !important;
}
#Threads .undo-redo-toast .content .action {
  color: #ffffff !important;
}
#Threads .undo-redo-toast .content .action .content-mask {
  background-color: #ffffff !important;
}
#Threads .undo-redo-toast .content .action .undo-action-text,
#Threads .undo-redo-toast .content .action .redo-action-text {
  color: #ffffff !important;
}
