css:
/*
Include the standard variables from Mailspring's base theme:
https://github.com/Foundry376/Mailspring/blob/master/app/static/base/ui-variables.less#L37:L40
*/
.send-later-popover .btn-cancel {
  width: 100%;
}
.btn-send-later .at {
  margin-left: 3px;
}
.send-later-status {
  display: flex;
  align-items: center;
}
.send-later-status .time {
  font-size: 0.9em;
  opacity: 0.62;
  color: #597ea0;
  font-weight: 400;
}
.send-later-status img {
  width: 38px;
  margin-left: 15px;
}
